INSERT INTO `bro_article` (`id`, `title`, `summary`, `posttime`, `uid`, `comefrom`, `content`, `keyword`, `pid`, `audit`, `recommend`, `allow`, `views`) VALUES
(7, '研究中心简介', '移动互联网定位关键技术、移动互联网创新应用开发', 1374649846, 1, '原创', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;北京邮电大学移动计算与智能感知技术研究中心(Mobile Computing &amp;Intelligent Sensing Technical Research Center )，成立于2005年，隶属于北京邮电大学软件学院，拥有6名教师，3名副教授，5位老师具有博士学位。</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;从2005年开始至今，本研究中心一直在移动互联网定位关键技术、移动互联网创新应用开发方面进行深入的研究，已拥有良好的研究基础，先后承担了多项无线定位技术相关科研课题，如国家自然科学基金课题&ldquo;无线传感器网络定位技术研究&rdquo;(60873244) 、&ldquo;自适应室内无线信号变化的低代价高精度定位技术研究&rdquo;（60973110）、北京市经信委项目&ldquo;北京邮电大学智慧大厦示范项目&rdquo;、江苏省科技支撑计划项目&ldquo;复杂室内环境全空间定位服务关键技术及应急安防应用示范&rdquo;（BE2010017）以及参与国家863计划课题、&ldquo;高精度高鲁棒性室内定位关键技术及装置研究&rdquo;(2007AA12Z321)和&ldquo;支持无线标准的低功耗智能定位终端及系统研发&rdquo;(2007AA01Z305)等，在移动互联网创新应用开发方面承担了江苏怡海软件科技有限公司合作项目&ldquo;智慧移动电脑系统开发&rdquo;<a rel=&quot;nofollow&quot;>（</a>iOS平台）、易八方（北京）软件开发有限公司合作项目&ldquo;移动互联网智能终端桌面控制系统开发&rdquo;<a rel=&quot;nofollow&quot;>（</a>iOS和Android平台）、生命在线（北京）科技有限公司合作项目&ldquo;智慧医疗产品软件开发&rdquo;（iOS、Android、Windows Phone 7平台）等多项移动终端软件开发项目，积累了大量的移动互联网关键终端平台开发经验。</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;科研方面，本研究中心还与中国科学院计算研究所合作，在全空间定位测量数据预处理、鲁棒估计、多节点协同定位以及智能定位跟踪系统等方面取得了系列科研成果，在移动互联网创新应用方面积累了丰富的经验，在国内外知名学术期刊和学术会议上发表无线定位相关论文80多篇（详见下面的相关学术论文列表），获得国家发明专利5项、申请了定位相关技术发明专利12项，在2011年度微软全球软件设计大赛中获得中国区一等奖（Windows Phone 7平台），并作为唯一团队在2011年度微软中国技术大会上做成果展示。</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;教学方面，由团队教师为主为北京邮电大学工学硕士教授&ldquo;移动计算中的软件工程&rdquo;课程，全面讲授移动互联网特性及开发技术，为在校学生讲解行业发展趋势、传授前沿开发技术，得到了学生一致好评。</p>\r\n', '移动，互联网，研究中心', 1, 1, 1, 0, 15),
(9, '基于LBS的智能提醒平台的设计与实现', '本项目研究基于LBS的智能提醒平台，主要功能有基于位置的备忘、任务以及基于位置的个性化推荐。本项目实现了一个有NoSQL数据库驱动的node.js架构的服务器，同时开发了具备定位、提醒、分享、第三方账号登录技术特性的Android客户端，已能给用户带来较好的使用体验和服务。', 1374650506, 2, '原创', '<div class=&quot;multicntwrap&quot; style=&quot;white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(59,59,59); text-align: left; font: 12px arial, helvetica, simsun, u5b8bu4f53; orphans: 2; widows: 2; letter-spacing: normal; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px&quot;>\r\n	<div class=&quot;multicnt&quot;>\r\n		<div>\r\n			<p style=&quot;white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(59,59,59); padding-bottom: 0px; text-align: left; padding-top: 0px; font: 14px/25px arial, helvetica, simsun, u5b8bu4f53; padding-left: 0px; margin: 0px 0px 10px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px&quot;>\r\n				<span style=&quot;line-height: 25px&quot;>关键词：<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>LBS&nbsp;&nbsp;</span>提醒<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>&nbsp; Android<span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;font-size: 11pt; font-family: 黑体; text-align: right; line-height: 27px&quot;><span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;height: auto; line-height: 25px&quot;><img alt=&quot;基于LBS的智能提醒平台的设计与实现 - lxrhzmlh - lxrhzmlh的博客&quot; src=&quot;/brocms/public/uploads/20130725133126_976.png&quot; style=&quot;border-left-width: 0px; max-width: 100%; height: 236px; border-right-width: 0px; border-bottom-width: 0px; float: right; margin: 10px 0px 10px 10px; line-height: 22px; border-top-width: 0px; width: 340px; border-image: initial&quot; /></span></span></span></span></span></span></p>\r\n			<div style=&quot;white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(59,59,59); text-align: right; font: 14px/25px arial, helvetica, simsun, u5b8bu4f53; orphans: 2; widows: 2; letter-spacing: normal; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px&quot;>\r\n				<div style=&quot;font-family: 黑体; text-align: left; line-height: 22px&quot;>\r\n					<p>\r\n						<strong><span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;line-height: 22px&quot;><font size=&quot;3&quot; style=&quot;line-height: 28px&quot;><strong>项目成员信息：</strong></font></span></span></strong></p>\r\n				</div>\r\n				<div style=&quot;text-align: left; line-height: 25px&quot;>\r\n					<div style=&quot;line-height: 25px&quot;>\r\n						<div style=&quot;font-family: arial, helvetica, sans-serif; line-height: 22px&quot;>\r\n							<p>\r\n								<span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;font-size: 11pt; font-family: 黑体; text-align: right; line-height: 27px&quot;>潘昆豪&nbsp;&nbsp;2010级本科&nbsp;软件学院&nbsp;软件工程专业</span></span></p>\r\n							<p>\r\n								<span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;font-size: 11pt; font-family: 黑体; text-align: right; line-height: 27px&quot;>杜潘洋&nbsp;&nbsp;2010级本科&nbsp;软件学院&nbsp;软件工程专业</span></span></p>\r\n							<p>\r\n								<span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;font-size: 11pt; font-family: 黑体; text-align: right; line-height: 27px&quot;>任兵飞&nbsp;&nbsp;2010级本科&nbsp;软件学院&nbsp;软件工程专业</span></span></p>\r\n							<p>\r\n								<span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;font-size: 11pt; font-family: 黑体; text-align: right; line-height: 27px&quot;>郭晓晖&nbsp;&nbsp;2010级本科&nbsp;软件学院&nbsp;软件工程专业</span></span></p>\r\n							<p>\r\n								<span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;font-size: 11pt; font-family: 黑体; text-align: right; line-height: 27px&quot;>王佳慧&nbsp;&nbsp;2010级本科&nbsp;软件学院&nbsp;软件工程专业</span></span></p>\r\n						</div>\r\n					</div>\r\n				</div>\r\n			</div>\r\n			<div style=&quot;white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(59,59,59); text-align: left; font: 14px/25px arial, helvetica, simsun, u5b8bu4f53; orphans: 2; widows: 2; letter-spacing: normal; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px&quot;>\r\n				<div style=&quot;line-height: 25px&quot;>\r\n					<div style=&quot;line-height: 22px&quot;>\r\n						&nbsp;</div>\r\n					<div style=&quot;line-height: 25px&quot;>\r\n						<b style=&quot;line-height: 25px&quot;>指导教师信息：</b></div>\r\n					<div style=&quot;line-height: 25px&quot;>\r\n						&nbsp;</div>\r\n					<div style=&quot;line-height: 25px&quot;>\r\n						&nbsp;<span style=&quot;line-height: 25px&quot;>赵 方<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>&nbsp;&nbsp;</span>软件学院<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>&nbsp;</span>研究方向：移动计算，智能感知</span></div>\r\n					<div style=&quot;line-height: 25px&quot;>\r\n						&nbsp;</div>\r\n				</div>\r\n				<p style=&quot;padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px; line-height: 25px; padding-right: 0px&quot;>\r\n					<span style=&quot;line-height: 25px&quot;><b style=&quot;line-height: 25px&quot;>项目背景及简介：</b></span></p>\r\n				<p style=&quot;padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px; line-height: 21px; padding-right: 0px; text-indent: 24pt&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>随着城市化不断发展，人们的生活节奏越来越快、事情太多以致我们无意中会错过很多我们喜欢或者应该做的事情。很多时候我们会经过附近的地方，路过却会错过，本应用就是为了此种遗憾而开发。与传统基于时间的提醒模式不同，本团队将着眼点关注于基于地理位置的提醒模式，结合日常生活的备忘事件，加入社交功能，给用户提供更加周到、高效的服务，打破当前提醒无法同位置信息结合的局面。</span></p>\r\n				<p style=&quot;padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px; line-height: 21px; padding-right: 0px; text-indent: 24pt&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>本项目研究基于<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>LBS</span>的智能提醒平台，主要功能有基于位置的备忘、任务以及基于位置的个性化推荐。本项目实现了一个有<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>NoSQL</span>数据库驱动的<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>node.js</span>架构的服务器，同时开发了具备定位、提醒、分享、第三方账号登录技术特性的<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>Android</span>客户端，已能给用户带来较好的使用体验和服务。</span></p>\r\n				<p style=&quot;padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px; line-height: 25px; padding-right: 0px&quot;>\r\n					<strong style=&quot;line-height: 25px&quot;><span style=&quot;line-height: 25px&quot;>项目研究创新点</span><span style=&quot;line-height: 25px&quot;>：</span></strong></p>\r\n				<p style=&quot;padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px; line-height: 21px; padding-right: 0px; text-indent: 24pt&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>gNote</span><span style=&quot;line-height: 25px&quot;>是一个基于地点的智能提醒平台，能够对用户的日常生活进行监督和提醒。用户可以使用<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>Android</span>客户端，设置基于地点的备忘和任务，以及接受个性化提醒。但与其他提醒应用不同的是：</span></p>\r\n				<p style=&quot;line-height: 25px&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>备忘不再仅仅基于时间轴，而是加入地理位置信息，这样弥补了纯时间备忘的缺陷；用户可以在任何地点设置备忘，到达或离开该地点即可对用户进行提醒。</span></p>\r\n				<p style=&quot;line-height: 25px&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>引入基于地点的任务模式：将LBS融入任务中，提出了一全新的日常任务监督模式。该模式可以使用户任务添加到某个地点，达到该地点即可完成一个任务。该模式促使用户进行身体力行，改变了传统的基于时间的任务模式，给用户带来全新体验。<img alt=&quot;基于LBS的智能提醒平台的设计与实现 - lxrhzmlh - lxrhzmlh的博客&quot; src=&quot;/brocms/public/uploads/20130725133125_334.png&quot; style=&quot;border-left-width: 0px; max-width: 100%; height: 214px; font-family: 黑体; border-right-width: 0px; border-bottom-width: 0px; float: right; margin: 10px 0px 10px 10px; line-height: 22px; border-top-width: 0px; width: 341px; border-image: initial&quot; /></span></p>\r\n				<p style=&quot;line-height: 25px&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>支持第三方平台登录系统。在当下互联网行业繁荣的今天，绝大部分用户在各大互联网服务商都注册过帐号。通过遵循OpenID、OAuth 2.0标准，本系统目前可以使用新浪微博帐号登录，这免除了用户在首次登陆时繁琐的注册流程，改善了用户的登录体验。同时还可通过新浪微博账号分享备忘、进行任务发布监督，使提醒平台更加社交化。</span></p>\r\n				<p style=&quot;line-height: 25px&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>创新的备忘形式。一般的备忘录应用主要以文字作为备忘内容，而客户端程序gNote除了文字的备忘方式外，还能够添加图片和录音。这免去了繁琐的文字输入过程，播放语音即可知晓备忘内容，给用户带来了更丰富的多媒体体验。</span></p>\r\n				<p style=&quot;line-height: 25px&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>服务器端使用node.js与mongoDB的架构，使得本应用能支持大量并发访问，容纳海量备忘数据，加快了与用户交互的速度与稳定性。</span></p>\r\n				<p style=&quot;line-height: 25px&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>引入个性化推荐模型：传统推荐不能针对特定人群，通常将同一推荐发送给所有用户，而这往往不是用户需要的。本应用通过挖掘用户GPS数据，建立基于位置的个人生活模式框架，从而针对不同用户的生活习惯进行个性化的推荐。我们通过基于密度的聚类和协同过滤技术，分析出用户的停留序列，进而对用户推荐好友或提前进行下一地点提醒。</span></p>\r\n				<p style=&quot;padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 21pt; line-height: 21px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<strong style=&quot;line-height: 25px&quot;><span style=&quot;line-height: 25px&quot;>项目成果<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>:</span></span></strong></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px; line-height: 17px; padding-right: 0px; text-indent: 21pt&quot;>\r\n					<span style=&quot;line-height: 25px&quot;>本项目主要着眼于应用创新；在项目的研究过程中，主要的研究内容以及研究成果如下：</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 18pt; line-height: 17px; padding-right: 0px; text-indent: -18pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: 宋体; line-height: 17px&quot;>1．<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>基于位置的备忘及任务</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 39pt; line-height: 17px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>利用<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>Android</span>智能手机自带的<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>GPS</span>模块和蜂窝网络，实现定位服务；</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 39pt; line-height: 17px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>研究并开发通过网络服务器的分享和推送；</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 39pt; line-height: 17px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>研究并设计基于地理位置的提醒框架和算法；</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 39pt; line-height: 17px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp; &nbsp; &nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>研究并设计基于地理位置的信息处理和表现形式；</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 18pt; line-height: 17px; padding-right: 0px; text-indent: -18pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: 宋体; line-height: 17px&quot;>2．<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>基于位置的个性化推荐</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 39pt; line-height: 17px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>挖掘地理位置信息数据；</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 39pt; line-height: 17px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;&nbsp;&nbsp;&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>研究基于地点的个人生活模式；</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 39pt; line-height: 17px; padding-right: 0px; text-indent: -21pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp; &nbsp;<span class=&quot;Apple-converted-space&quot;>&nbsp;</span></span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>构建并设计个性化推荐的原型；</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 18pt; line-height: 17px; padding-right: 0px; text-indent: -18pt&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;font-family: 宋体; line-height: 17px&quot;>3．<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px&quot;>同时，本团队还研究并实现了以下内容：</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 18pt; line-height: 17px; padding-right: 0px; text-indent: -24px&quot;>\r\n					<span lang=&quot;EN-US&quot; style=&quot;line-height: 17px; text-indent: -21pt&quot;><font face=&quot;宋体&quot; style=&quot;line-height: 25px&quot;>&nbsp;&nbsp; &nbsp;</font></span><span lang=&quot;EN-US&quot; style=&quot;font-family: symbol; line-height: 17px; text-indent: -21pt&quot;>&middot;<span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal&quot;>&nbsp; &nbsp; &nbsp;</span></span><span style=&quot;font-family: 宋体; line-height: 17px; text-indent: -21pt&quot;>遵循<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>OpenID</span>和<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>OAuth 2.0</span>标准，允许用户通过第三方平台的账号登陆，极大地提升了用户的登陆体验，省去了用户首次使用本应用所需要的繁琐的注册流 程。</span></p>\r\n				<p style=&quot;vertical-align: baseline; padding-bottom: 0px; padding-top: 0px; padding-left: 0px; margin: 0px 0px 10px 18pt; line-height: 17px; padding-right: 0px; text-indent: -24px&quot;>\r\n					<span style=&quot;line-height: 17px; text-indent: -21pt&quot;><font face=&quot;宋体&quot; style=&quot;line-height: 25px&quot;>&nbsp;<span class=&quot;Apple-converted-space&quot;>&nbsp;</span></font></span><span style=&quot;font-family: symbol; line-height: 17px; text-indent: -21pt&quot;>&nbsp;&nbsp; &middot;</span><span style=&quot;font-size: 7pt; font-family: &#039;times new roman&#039;; line-height: normal; text-indent: -21pt&quot;>&nbsp;&nbsp;</span><span style=&quot;line-height: 25px; text-indent: -21pt&quot;>&nbsp;&nbsp;</span><span style=&quot;font-size: 10.5pt; font-family: 宋体; line-height: 25px; text-indent: -21pt&quot;>智能手机定位技术的优化，通过动态预测用户经过的距离，减少<span lang=&quot;EN-US&quot; style=&quot;line-height: 25px&quot;>GPS</span>数据上传次数，</span><span style=&quot;font-size: 10.5pt; font-family: 宋体; line-height: 25px; text-indent: -21pt&quot;>达到节能省电的功能。</span></p>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n', 'LBS,提醒,Android', 4, 1, 0, 0, 25),
(4, '移动计算与智能感知技术研究中心', '研究方向：移动计算与移动定位技术、智能信息处理、新能源关键技术、物联网关键技术、大数据处理、数据挖掘、移动终端开发', 1374545351, 1, '原创', '<p style=&quot;&quot;>\r\n	<strong>中心名称：移动计算与智能信息处理研究中心</strong></p>\r\n<p>\r\n	<strong>研究方向：</strong>移动计算与移动定位技术、智能信息处理、新能源关键技术、物联网关键技术、大数据处理、数据挖掘、移动终端开发</p>\r\n<p>\r\n	<strong>师资队伍：</strong>教师10名，其中教授1名、岗位教授2名、副教授3名、讲师4名，大部分具有博士学位。工学硕士导师：杨正球教授、赵方教授、刘辰副教授、李朝晖副教授、何所惧副教授（照片秦老师帮忙选）</p>\r\n<p>\r\n	<strong>科研基础：</strong>近几年在相关领域开展了深入研究，在国家自然基金、科技部863项目、国家科技支撑项目、科技部重大专项、工信部物联网专项资金、省部级科技支撑等纵项课题，以及与中国普天科技有限公司、中国新浪科技有限公司等横向课题支撑下取得丰硕研究成果，5年内发表论文130余篇，其中SCI 3篇、EI检索60余篇，获得国家发明专利3项，申请专利10余项，软件著作权10余项，专著20余部。</p>\r\n<p>\r\n	<strong>本中心科研经费充足，5</strong><strong>年达到800</strong><strong>万元</strong></p>\r\n<p>\r\n	<strong>学术成果及获奖情况：</strong></p>\r\n<ol>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		获信息产业部科技进步一等奖1项、二等奖1项、北京市科技进步二等奖1项</li>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		&ldquo;China Communications&rdquo;、&ldquo;Chinese Journal of Electronics&rdquo;等SCI刊物发表论文3篇</li>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		在《计算机研究与发展》、《通信学报》、《自动化学报》、《高技术通讯》、《电子与信息学报》等国家一级EI刊物以及&ldquo;Mobile Ad-hoc and Sensor Networks&rdquo;等国际会议发表60余篇EI论文</li>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		获得国家发明专利3项、申请专利10余项、获得软件著作权10余项</li>\r\n</ol>\r\n<p>\r\n	<strong>教学情况：</strong>本中心在《高级操作系统》、《移动计算中的软件工程》、《编译原理》、《数据库》等10余门课程，在校教学观摩比赛、微软Image Cup等软件设计大赛上均获得好成绩</p>\r\n<p>\r\n	<strong>学术文化氛围：</strong></p>\r\n<ol>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		<strong>晴耕</strong>&mdash;项目负责教师-&gt;项目组长（学生）-&gt;项目骨干（学生），紧密沟通，在实际项目中锻炼学生开发技能，提升求职能力</li>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		<strong>雨读</strong>&mdash;每周一次的学术沙龙，老师和学生一起阅读、讨论研究方向国际优秀学术论文，培养学生学术能力</li>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		<strong>师兄、师弟面对面</strong>&mdash;定期举行往届、应届学生见面会，交流求职、方向、社会需求</li>\r\n	<li style=&quot;margin-left: 21pt; &quot;>\r\n		<strong>我们是一家人</strong>&mdash;定期一起体育活动、过生日、聚餐创造了良好的团队氛围</li>\r\n</ol>\r\n<p>\r\n	<strong>学生就业：</strong></p>\r\n<p>\r\n	本中心毕业生大部分就职于银行、电信等大型国有企业、IBM等外企、百度等IT高技术企业，已逐步发展为人才培养、科学研究和成果转化、健康发展的科研基地。</p>\r\n<p style=&quot;margin-left:21.0pt;&quot;>\r\n	<img alt=&quot;&quot; src=&quot;/brocms/public/uploads/4/20130723184554_252.jpg&quot; style=&quot;width: 500px; height: 108px; margin-left: 60px; margin-right: 60px; &quot; /></p>\r\n<p align=&quot;center&quot;>\r\n	欢迎加入&ldquo;移动计算与智能信息处理研究中心&rdquo;大家庭！</p>\r\n<p align=&quot;center&quot;>\r\n	让我们一起进步、成长！</p>\r\n', '移动计算，智能感知', 7, 1, 1, 0, 47),
(6, '导师队伍', '移动互联网', 1374626935, 1, '原创', '<p>\r\n	&nbsp;</p>\r\n<div style=&quot;line-height: 25px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	<img alt=&quot;309 - soulrelay_bupt2012 - soulrelay&quot; src=&quot;/brocms/public/uploads/20130724110551_638.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 10px 10px 10px 0px; width: 130px; height: 183px; float: left; &quot; /></div>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	&nbsp;<b><span style=&quot;font-family: 宋体; &quot;>赵方</span></b><span style=&quot;font-family: 宋体; &quot;>，女，博士，北京邮电大学岗位教授（副教授）。</span>中国计算机学会会员；国家自然基金、浙江省、江苏省自然基金、省科技支撑项目评审专家；电子与信息学报、邮电高校学报论文评审专家。主要研究方向包括移动计算、移动定位技术、LBS关键技术、移动大数据处理、物联网关键技术、云端设备接入技术等。近年来，作为项目负责人，承担国家自然基金项目1项，作为子课题负责人，参与科技部重大专项2项，科技部863课题2项，工信部物联网专项资金项目1项，国家自然基金子课题项目1项，北京市自然基金子课题项目1项，主持江苏省科技支撑重大项目1项，广东省院产学研重大合作项目1项，参与国家教委双语示范课程1项，北京邮电大学物联网支撑平台项目1项，作为项目负责人和主研人参与企业合作项目40多项。在国内外知名期刊和重要会议发表论文70余篇，编（译）著书籍3册，申请发明专利20余项，已授权专利5项，软件著作权15项。</p>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	<span lang=&quot;EN-US&quot;>&nbsp;</span></p>\r\n<div style=&quot;line-height: 25px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; background-color: rgb(21, 21, 21); &quot;>\r\n	<img alt=&quot;309 - soulrelay_bupt2012 - soulrelay&quot; src=&quot;/brocms/public/uploads/20130724124223_495.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 10px 10px 10px 0px; width: 130px; height: 167px; float: left; &quot; /></div>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	&nbsp;<b><span style=&quot;font-family: 宋体; &quot;>李朝晖</span></b><span style=&quot;font-family: 宋体; &quot;>，女，博士，北京邮电大学副教授。</span><span lang=&quot;EN-US&quot; style=&quot;font-family: 宋体; &quot;>2004</span><span style=&quot;font-family: 宋体; &quot;>年博士毕业于华南理工大学，<span lang=&quot;EN-US&quot;>2006</span>年获国家留学基金委资助赴澳大利亚悉尼大学做访问学者。主要研究方向：数字图像处理、多媒体技术、移动互联网应用等。主持或参与科研项目多项，在国内、国际会议和刊物上发表论文多篇。具有良好的项目管理经验。主讲&ldquo;算法分析与设计&rdquo;、&ldquo;多媒体技术&rdquo;等课程。近<span lang=&quot;EN-US&quot;>3</span>年，李朝晖副教授承担了中国移动公司项目&ldquo;移动互联网舆情分析及系统开发&rdquo;、新浪公司项目&ldquo;新浪公司<span lang=&quot;EN-US&quot;>-</span>微乐汇<span lang=&quot;EN-US&quot;>1.5</span>版本开发&rdquo;（已上线）、&ldquo;蓝色港湾基于位置的移动广告推送系统开发&rdquo;等多项移动互联网项目，具有丰富的移动互联网应用开发管理经验。</span></p>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	<span lang=&quot;EN-US&quot;>&nbsp;</span></p>\r\n<div style=&quot;line-height: 25px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; background-color: rgb(21, 21, 21); &quot;>\r\n	<img alt=&quot;309 - soulrelay_bupt2012 - soulrelay&quot; src=&quot;/brocms/public/uploads/20130724124223_609.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 10px 10px 10px 0px; width: 175px; height: 114px; float: left; &quot; /></div>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	&nbsp;<b><span style=&quot;font-family: 宋体; &quot;>何所俱</span></b><span style=&quot;font-family: 宋体; &quot;>，男，博士，北京邮电大学副教授。主要研究方向为：移动互联网软件、移动智能信息处理、数据挖掘、人工智能、游戏智能、信息系统、软件工程等。有多年北美</span><span lang=&quot;EN-US&quot;>IT</span><span style=&quot;font-family: 宋体; &quot;>项目工作经历，其中在</span><span lang=&quot;EN-US&quot;>2000</span><span style=&quot;font-family: 宋体; &quot;>年</span><span lang=&quot;EN-US&quot;>1</span><span style=&quot;font-family: 宋体; &quot;>月</span><span lang=&quot;EN-US&quot;>--2002</span><span style=&quot;font-family: 宋体; &quot;>年</span><span lang=&quot;EN-US&quot;>1</span><span style=&quot;font-family: 宋体; &quot;>月期间曾任职原加拿大</span><span lang=&quot;EN-US&quot;>&nbsp;Nortel Networks</span><span style=&quot;font-family: 宋体; &quot;>，担任软件研发工程师，参与并完成多个光纤通讯项目的软件研发。自</span><span lang=&quot;EN-US&quot;>2004</span><span style=&quot;font-family: 宋体; &quot;>年</span><span lang=&quot;EN-US&quot;>9</span><span style=&quot;font-family: 宋体; &quot;>月始在北邮软件学院任教至今，曾担任多个科研课题负责人。作为项目负责人和主研人参与企业合作项目</span><span lang=&quot;EN-US&quot;>5</span><span style=&quot;font-family: 宋体; &quot;>项。在国内外知名学术期刊和重要会议发表论文</span><span lang=&quot;EN-US&quot;>20</span><span style=&quot;font-family: 宋体; &quot;>余篇</span>&nbsp;<span style=&quot;font-family: 宋体; &quot;>（其中</span><span lang=&quot;EN-US&quot;>EI</span><span style=&quot;font-family: 宋体; &quot;>检索</span><span lang=&quot;EN-US&quot;>19</span><span style=&quot;font-family: 宋体; &quot;>篇）。</span></p>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	<span lang=&quot;EN-US&quot;>&nbsp;</span></p>\r\n<div style=&quot;line-height: 25px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	<img alt=&quot;309 - soulrelay_bupt2012 - soulrelay&quot; src=&quot;/brocms/public/uploads/20130724124223_786.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 10px 10px 10px 0px; float: left; &quot; /></div>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	&nbsp;<b><span style=&quot;font-family: 宋体; &quot;>贾红娓</span></b><span style=&quot;font-family: 宋体; &quot;>，女。博士，北京邮电大学讲师。</span><span lang=&quot;EN-US&quot;>2004</span><span style=&quot;font-family: 宋体; &quot;>年至</span><span lang=&quot;EN-US&quot;>2007</span><span style=&quot;font-family: 宋体; &quot;>年在中国科学院计算技术研究所博士学位研究生，从事企业信息化、计算机支持的协同工作方向的研究工作。</span><span lang=&quot;EN-US&quot;>2005</span><span style=&quot;font-family: 宋体; &quot;>年至</span><span lang=&quot;EN-US&quot;>2006</span><span style=&quot;font-family: 宋体; &quot;>年在美国密西根大学计算机专业访问学者。</span><span lang=&quot;EN-US&quot;>1986</span><span style=&quot;font-family: 宋体; &quot;>年至</span><span lang=&quot;EN-US&quot;>1990</span><span style=&quot;font-family: 宋体; &quot;>年在上海交通大学获学士学位。参与</span><span lang=&quot;EN-US&quot;>2</span><span style=&quot;font-family: 宋体; &quot;>项国家级项目，参与省部级多项项目，主持和参与多项横向课题。目前研究方向为物联网系统中基于位置的服务，软件的可用性等</span><span lang=&quot;EN-US&quot;>&nbsp;</span><span style=&quot;font-family: 宋体; &quot;>。被</span><span lang=&quot;EN-US&quot;>SCI</span><span style=&quot;font-family: 宋体; &quot;>和</span><span lang=&quot;EN-US&quot;>EI</span><span style=&quot;font-family: 宋体; &quot;>检索收录</span><span lang=&quot;EN-US&quot;>6</span><span style=&quot;font-family: 宋体; &quot;>篇论文（其中有</span><span lang=&quot;EN-US&quot;>2</span><span style=&quot;font-family: 宋体; &quot;>篇有关移动互联网中路径规划），出版著作</span><span lang=&quot;EN-US&quot;>5</span><span style=&quot;font-family: 宋体; &quot;>部。近年来，贾红娓老师承担了&ldquo;北京市经信委重点项目</span><span lang=&quot;EN-US&quot;>-</span><span style=&quot;font-family: 宋体; &quot;>北京邮电大学智慧大厦</span><span lang=&quot;EN-US&quot;>&lt;</span><span style=&quot;font-family: 宋体; &quot;>基于位置的智慧餐饮、智慧会议室</span><span lang=&quot;EN-US&quot;>&gt;</span><span style=&quot;font-family: 宋体; &quot;>&rdquo;项目、德威公司&ldquo;留学生守护神（</span><span lang=&quot;EN-US&quot;>iOS</span><span style=&quot;font-family: 宋体; &quot;>和</span><span lang=&quot;EN-US&quot;>Android</span><span style=&quot;font-family: 宋体; &quot;>平台，该项目将推广到美国、加拿大、英国）&rdquo;</span>&nbsp;<span style=&quot;font-family: 宋体; &quot;>项目、北京市生命在线技术有限公司&ldquo;基于移动互联网的智慧医疗系统开发&rdquo;项目等多项课题，具有丰富的移动互联网应用开发管理经验。</span></p>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; &quot;>\r\n	<span lang=&quot;EN-US&quot;>&nbsp;</span></p>\r\n<div style=&quot;line-height: 25px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal; background-color: rgb(21, 21, 21); &quot;>\r\n	<img alt=&quot;309 - soulrelay_bupt2012 - soulrelay&quot; src=&quot;/brocms/public/uploads/20130724124223_152.jpg&quot; style=&quot;border: 0px; max-width: 100%; margin: 10px 10px 10px 0px; width: 120px; height: 180px; float: left; &quot; /></div>\r\n<p style=&quot;line-height: 25px; margin: 0px 0px 10px; padding: 0px; color: rgb(76, 76, 76); font-family: Arial, Helvetica, simsun, u5b8bu4f53; font-size: 14px; white-space: normal;  &quot;>\r\n	&nbsp;<b><span style=&quot;font-family: 宋体; &quot;>牛琨</span></b><span style=&quot;font-family: 宋体; &quot;>，女，博士，北京邮电大学讲师。</span><span lang=&quot;EN-US&quot;>2007</span><span style=&quot;font-family: 宋体; &quot;>年毕业于北京邮电大学网络与交换国家重点实验室，获通信与信息系统专业博士学位。主要研究方向为：数据挖掘、知识发现、商业智能、决策支持。并长期致力于数据挖掘、知识发现及其在电信领域的应用研究。</span><span lang=&quot;EN-US&quot;>2005</span><span style=&quot;font-family: 宋体; &quot;>年至今在国内外期刊会议上发表多篇中英文学术论文。作为项目负责人主持教育部青年科研创新计划专项课题&ldquo;基于机器学习的客户数据预测理论研究及工具实现&rdquo;和&ldquo;通信行业大数据的知识发现关键技术研究&rdquo;。</span><span style=&quot;font-family: 宋体; &quot;>在国内外期刊及会议上发表中英文学术论文数十篇，其中<span lang=&quot;EN-US&quot;>EI</span>检索<span lang=&quot;EN-US&quot;>8</span>篇。</span></p>\r\n', '博士，导师', 2, 1, 1, 0, 28);
INSERT INTO `bro_article` (`id`, `title`, `summary`, `posttime`, `uid`, `comefrom`, `content`, `keyword`, `pid`, `audit`, `recommend`, `allow`, `views`) VALUES
(13, '时间戳——基于社交网络的智能移动助理软件', '“时间戳”是一款基于社交网络模型的移动助理应用，提供个人时间的协同管理平台，同时融合了更为科学的时间管理模型与地理位置服务,为用户创造更理性的时间规划和更便捷的移动生活。', 1374657056, 2, '原创', '<p style=&quot;white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(59,59,59); padding-bottom: 0px; text-align: left; padding-top: 0px; font: 14px/25px arial, helvetica, simsun, u5b8bu4f53; padding-left: 0px; margin: 0px 0px 10px; orphans: 2; widows: 2; letter-spacing: normal; padding-right: 0px; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px&quot;>\r\n	<span style=&quot;font-family: 宋体; line-height: 25px; background-color: white; background-origin: initial; background-clip: initial&quot;>关键词：社会化　　时间效率　　位置辅助<img alt=&quot;2013年07月24日 - lxrhzmlh - lxrhzmlh的博客&quot; height=&quot;373&quot; src=&quot;/brocms/public/uploads/20130725133126_416.png&quot; style=&quot;border-left-width: 0px; max-width: 100%; height: 258px; border-right-width: 0px; border-bottom-width: 0px; float: right; margin: 10px 0px 10px 10px; line-height: 25px; border-top-width: 0px; width: 322px; border-image: initial&quot; width=&quot;499&quot; /></span></p>\r\n<p>\r\n	<b style=&quot;font-size: medium; font-family: 黑体; line-height: 25px&quot;><font size=&quot;4&quot;><span style=&quot;height: auto; line-height: 25px&quot;><span style=&quot;line-height: 22px&quot;><font size=&quot;3&quot; style=&quot;line-height: 28px&quot;><strong>项目成员信息：</strong></font></span></span></font></b></p>\r\n<div style=&quot;white-space: normal; text-transform: none; word-spacing: 0px; color: rgb(59,59,59); text-align: left; font: 14px/25px arial, helvetica, simsun, u5b8bu4f53; orphans: 2; widows: 2; letter-spacing: normal; background-color: rgb(255,255,255); text-indent: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px&quot;>\r\n	<div style=&quot;text-align: right; line-height: 25px&quot;>\r\n		<div style=&quot;text-align: left; line-height: 25px&quot;>\r\n			<div style=&quot;li